import './App.css';

function App() {
  return (
    <div className="App">
        <Header />
        <Section1 />
    </div>
  );
}
function Header() {
    return(
        <nav>
            <a href="#">Section1</a>
            <a href="#">Section2</a>
            <a href="#">Section3</a>
            <a href="#">Section4</a>
        </nav>
    )
}

function Section1() {
    return(
        <section>
            <p>React (иногда React.js или ReactJS) — JavaScript-библиотека[4] с открытым исходным кодом для разработки
                пользовательских интерфейсов. React разрабатывается и поддерживается Facebook, Instagram и сообществом
                отдельных разработчиков и корпораций[5][6][7]. React может использоваться для разработки
                одностраничных и мобильных приложений. Его цель — предоставить высокую скорость, простоту и масштабируемость.
                В качестве библиотеки для разработки пользовательских интерфейсов React часто используется с
                другими библиотеками, такими как MobX, Redux и GraphQL[en].</p>
        </section>
    )

}



export default App;
//./-файл назодится в той же папке что и
// компонента - это функция которая возвращает разметку (JSX) также это тег
